"use strict";for(var today=document.querySelector("input[name=date]").value,removeBtn=document.querySelectorAll("button.removeBtn"),i=0;i<removeBtn.length;i++)removeBtn[i].addEventListener("click",function(e){var e=e.target.parentElement.parentElement,t=e.querySelector(".siteName").innerText,r=e.querySelector(".distance").innerText,n=e.querySelector(".water").innerText,o=e.querySelector(".overTime").innerText,i=e.querySelector(".nightSupport").innerText,e=e.querySelector(".oiling").innerText;fetch("http://localhost:8282/db/record/remove-todays",{method:"POST",headers:{"Content-type":"application/json"},body:JSON.stringify({date:today,siteName:t,distance:r,water:n,overTime:o,nightSupport:i,oiling:e})}),window.location.reload()});